import React, { useState } from 'react';
import { MagnifyingGlassIcon, AdjustmentsHorizontalIcon } from '@heroicons/react/24/outline';

interface SearchBarProps {
  onSearch: (query: string) => void;
  onMoodSelect?: (mood: string) => void;
}

export const SearchBar = ({ onSearch, onMoodSelect }: SearchBarProps) => {
  const [query, setQuery] = useState('');
  const [showMoodFilters, setShowMoodFilters] = useState(false);

  const moods = [
    'Feeling overwhelmed',
    'Need inspiration',
    'Want to escape',
    'Looking for adventure',
    'Feeling nostalgic',
    'Want to learn',
    'Need a laugh',
    'Feeling romantic',
  ];

  const handleSubmit = (e: any) => {
    e.preventDefault();
    onSearch(query);
  };

  return (
    <div className="w-full">
      <form onSubmit={handleSubmit} className="relative">
        <div className="relative">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Search for books, authors, or moods..."
            className="input pl-10 pr-10"
          />
          <MagnifyingGlassIcon className="w-5 h-5 text-primary-500 absolute left-3 top-1/2 -translate-y-1/2" />
          <button
            type="button"
            onClick={() => setShowMoodFilters(!showMoodFilters)}
            className="absolute right-3 top-1/2 -translate-y-1/2 text-primary-500 hover:text-primary-700"
          >
            <AdjustmentsHorizontalIcon className="w-5 h-5" />
          </button>
        </div>
      </form>

      {/* Mood Filters */}
      {showMoodFilters && (
        <div className="absolute z-10 mt-2 w-full bg-white rounded-lg shadow-lg border border-primary-200">
          <div className="p-4">
            <h3 className="text-sm font-medium text-primary-900 mb-2">Search by mood</h3>
            <div className="grid grid-cols-2 gap-2">
              {moods.map((mood) => (
                <button
                  key={mood}
                  onClick={() => {
                    onMoodSelect?.(mood);
                    setShowMoodFilters(false);
                  }}
                  className="text-left text-sm text-primary-700 hover:bg-primary-50 p-2 rounded-lg"
                >
                  {mood}
                </button>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
