import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import { Layout } from './components/layout/Layout';
import { HomePage } from './components/features/HomePage';
import { ThreadPage } from './components/features/ThreadPage';
import { ThreadDetailPage } from './components/features/ThreadDetailPage';
import { BooksPage } from './components/features/BooksPage';
import { MoodDemo } from './components/features/MoodDemo';

function DebugInfo() {
  const location = useLocation();
  return (
    <div className="bg-yellow-100 border border-yellow-300 p-4 m-4 rounded">
      <h3 className="font-bold text-yellow-800">Debug Info:</h3>
      <p className="text-yellow-700">Current pathname: {location.pathname}</p>
      <p className="text-yellow-700">Current search: {location.search}</p>
      <p className="text-yellow-700">Current hash: {location.hash}</p>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Layout>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/threads" element={<ThreadPage />} />
          <Route path="/threads/:id" element={<ThreadDetailPage />} />
          <Route path="/books" element={<BooksPage />} />
          <Route path="/mood-demo" element={<MoodDemo />} />
          <Route path="*" element={<div>Page Not Found</div>} />
        </Routes>
      </Layout>
    </Router>
  );
}

export default App;
