import React from 'react';
import { SearchBar } from './SearchBar';
import { ThreadCard } from './ThreadCard';
import { BookCard } from './BookCard';

type Pace = 'Fast' | 'Moderate' | 'Slow';

interface Book {
  title: string;
  author: string;
  coverImage: string;
  pace: Pace;
  tone: string[];
  themes: string[];
  reviewSnippet: string;
  bestFor: string[];
}

export const HomePage = () => {
  // Mock data for demonstration
  const trendingBooks: Book[] = [
    {
      title: "Project Hail Mary",
      author: "Andy Weir",
      coverImage: "https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1597695864i/54493401.jpg",
      pace: "Fast" as Pace,
      tone: ["Hopeful", "Scientific"],
      themes: ["Space", "Friendship", "Survival"],
      reviewSnippet: "A brilliant mix of science, humor, and heart. Couldn't put it down!",
      bestFor: ["Sci-fi fans", "Problem solvers", "Space enthusiasts"]
    },
    {
      title: "Klara and the Sun",
      author: "Kazuo Ishiguro",
      coverImage: "https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1605039531i/54120408.jpg",
      pace: "Moderate" as Pace,
      tone: ["Thoughtful", "Poetic"],
      themes: ["AI", "Humanity", "Love"],
      reviewSnippet: "A beautiful exploration of what it means to be human",
      bestFor: ["Literary fiction", "Philosophical readers", "AI enthusiasts"]
    }
  ];

  const threads = [
    {
      title: "Books like Black Mirror",
      description: "Looking for mind-bending sci-fi that makes you question reality",
      upvotes: 234,
      comments: 45,
      timestamp: "2h ago",
      tags: ["Sci-Fi", "Dystopian", "Thriller"]
    },
    {
      title: "Page-turners for slump days",
      description: "Need something to get me out of my reading slump",
      upvotes: 189,
      comments: 32,
      timestamp: "4h ago",
      tags: ["Fast-paced", "Engaging", "Contemporary"]
    }
  ];

  return (
    <div className="space-y-12">
      {/* Hero Section */}
      <section className="text-center space-y-4">
        <h1 className="text-4xl font-bold text-primary-900">
          Discover Your Next Great Read
        </h1>
        <p className="text-xl text-primary-600 max-w-2xl mx-auto">
          Get personalized book recommendations based on your mood, interests, and reading style
        </p>
        <div className="max-w-2xl mx-auto">
          <SearchBar 
            onSearch={(query) => console.log('Search:', query)}
            onMoodSelect={(mood) => console.log('Mood:', mood)}
          />
        </div>
      </section>

      {/* Trending Books */}
      <section>
        <h2 className="text-2xl font-bold text-primary-900 mb-6">Trending Now</h2>
        <div className="grid gap-4 md:grid-cols-2">
          {trendingBooks.map((book) => (
            <BookCard key={book.title} {...book} />
          ))}
        </div>
      </section>

      {/* Popular Threads */}
      <section>
        <h2 className="text-2xl font-bold text-primary-900 mb-6">Popular Threads</h2>
        <div className="grid gap-4 md:grid-cols-2">
          {threads.map((thread) => (
            <div key={thread.title}>
              <ThreadCard
                title={thread.title}
                description={thread.description}
                upvotes={thread.upvotes}
                comments={thread.comments}
                timestamp={thread.timestamp}
                tags={thread.tags}
              />
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};
