    professions: ["Psychologists", "Economists", "Product Managers", "Executives", "Data Scientists"],
    isExternal: false,
    pageCount: 499,
    categories: ["Psychology", "Non-fiction", "Economics", "Science"]
  }
];

export const BooksPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [books, setBooks] = useState<Book[]>([]);
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(true);
  const [includeExternal, setIncludeExternal] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchType, setSearchType] = useState('all');

  // Handle URL search parameters when component loads
  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const queryFromUrl = queryParams.get('query');
    const typeFromUrl = queryParams.get('type');

    if (queryFromUrl) {
      setSearchQuery(queryFromUrl);
      if (typeFromUrl) {
        setSearchType(typeFromUrl);
      }
      // Fetch books with URL parameters
      fetchBooks(queryFromUrl, typeFromUrl || 'all', includeExternal);
    } else {
      // If no search parameters, fetch all books from API
      fetchAllBooks();
    }
  }, [location.search]);

  // Dedicated function to fetch all books
  const fetchAllBooks = async () => {
    setLoading(true);
    setError(null);

    try {
      console.log('Fetching all books from API');
      const data = await api.getBooks(includeExternal);

      // Process the books data
      const processedData = processBookData(data);

      console.log(`Fetched ${processedData.length} books from API`);
      setBooks(processedData);
    } catch (err) {
      console.error('Error fetching all books:', err);
      setError(err instanceof Error ? err.message : 'Failed to load books. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Helper function to process book data 
  const processBookData = (data: Book[]) => {
    return data.map(book => {
      // Process any string arrays that might be returned as JSON strings or objects
      let processedThemes = book.themes;
      let processedTone = book.tone;
      let processedProfessions = book.professions;
      let processedBestFor = book.bestFor;

      // Handle if themes is a string (potentially a stringified array)
      if (typeof book.themes === 'string') {
        try {
          // Try to parse as JSON
          processedThemes = JSON.parse(book.themes as string);
        } catch (e) {
          processedThemes = (book.themes as string).split(',').map((t: string) => t.trim());
        }
      }

      // Process arrays that might have objects or JSON strings
      if (Array.isArray(processedThemes)) {
        processedThemes = processedThemes.map((t: any) => extractValue(t));
      } else {
        processedThemes = [];
      }

      // Handle tone the same way
      if (typeof book.tone === 'string') {
        try {
          processedTone = JSON.parse(book.tone as string);
        } catch (e) {
          processedTone = (book.tone as string).split(',').map((t: string) => t.trim());
        }
      }

      if (Array.isArray(processedTone)) {
        processedTone = processedTone.map((t: any) => extractValue(t));
      } else {
        processedTone = [];
      }

      // Handle professions the same way
      if (typeof book.professions === 'string') {
        try {
          processedProfessions = JSON.parse(book.professions as string);
        } catch (e) {
          processedProfessions = (book.professions as string).split(',').map((p: string) => p.trim());
        }
      }

      if (Array.isArray(processedProfessions)) {
        processedProfessions = processedProfessions.map((p: any) => extractValue(p));
      } else {
        processedProfessions = [];
      }

      // Handle bestFor field
      if (typeof book.bestFor === 'string') {
        try {
          processedBestFor = JSON.parse(book.bestFor as string);
        } catch (e) {
          processedBestFor = (book.bestFor as string).split(',').map((b: string) => b.trim());
        }
      }

      if (Array.isArray(processedBestFor)) {
        processedBestFor = processedBestFor.map((b: any) => extractValue(b));
      } else {
        // Default to empty array if bestFor is null/undefined
        processedBestFor = [];
      }

      // Process pace field - ensure it's always a string
      let processedPace: string = "Moderate";  // Default value

      if (book.pace) {
        if (typeof book.pace === 'object') {
          // Safely access the value property
          const paceObj = book.pace as PaceObject;
          processedPace = paceObj.value || "Moderate";
        } else if (typeof book.pace === 'string') {
          processedPace = book.pace;
        }
      }

      // Return processed book data with clean fields
      return {
        ...book,
        pace: processedPace as Pace,
        tone: processedTone,
        themes: processedThemes,
        professions: processedProfessions,
        bestFor: processedBestFor
      };
    });
  };

  const fetchBooks = async (
    query: string = '', 
    type: string = 'all', 
    useExternal: boolean = includeExternal
  ) => {
    setLoading(true);
    setError(null);

    try {
      let data: Book[];

      if (query) {
        console.log(`Searching for "${query}" in category "${type}"`);
        data = await api.searchBooks(query, type, useExternal);
      } else {
        console.log('Fetching all books');
        data = await api.getBooks(useExternal);
      }

      // Process the book data
      const processedData = processBookData(data);

      console.log('Processed books:', processedData);
      setBooks(processedData);

      // Log total book count
      console.log(`Total books to display: ${processedData.length}`);
    } catch (err) {
      console.error('Error processing books:', err);
      setError(err instanceof Error ? err.message : 'Failed to load data. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Handler for SearchBar
  const handleSearch = (query: string, type: string = 'all') => {
    setSearchQuery(query);
    setSearchType(type);
    fetchBooks(query, type, includeExternal);

    // Update URL to reflect the search
    const searchParams = new URLSearchParams();
    if (query) {
      searchParams.set('query', query);
      if (type !== 'all') {
        searchParams.set('type', type);
      }
      navigate(`/books?${searchParams.toString()}`);
    } else {
      navigate('/books');
    }
  };

  const handleExternalToggle = () => {
    const newIncludeExternal = !includeExternal;
    setIncludeExternal(newIncludeExternal);
    fetchBooks(searchQuery, searchType, newIncludeExternal);
  };

  if (loading) {
    return <div className="text-center py-10">Loading books...</div>;
  }

  if (error) {
    return <div className="text-center py-10 text-red-600">Error: {error}</div>;
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="text-center space-y-4">
        <h1 className="text-3xl font-bold text-primary-900">Book Recommendations</h1>
        <p className="text-lg text-primary-600">
          Discover books that match your interests and reading style
        </p>
      </div>

      {/* Search */}
      <div className="max-w-3xl mx-auto">
        <SearchBar 
          onSearch={handleSearch}
          onMoodSelect={(mood) => handleSearch(mood, 'mood')}
        />

        {/* External API Toggle */}
        <div className="flex items-center justify-end mt-4 space-x-2">
          <span className="text-sm text-gray-600">Include more books from external sources</span>
          <Switch
            checked={includeExternal}
            onChange={handleExternalToggle}
            className="data-[state=checked]:bg-green-500"
          />
        </div>
      </div>

      {/* Books Grid */}
      {books.length === 0 ? (
        <div className="text-center py-10">No books found. Try a different search or enable external sources.</div>
      ) : (
        <div>
          {/* Search result info */}
          <div className="mb-4 text-sm text-gray-600">
            {searchQuery ? 
              `Found ${books.length} books for "${searchQuery}"${searchType !== 'all' ? ` by ${searchType}` : ''}` :
              `Featuring ${books.length} of our top books across different categories`
            }
          </div>

          {/* Books grid */}
          <div className="grid gap-6 grid-cols-1 lg:grid-cols-2">
            {books.map((book) => (
              <BookCard
                key={book.id || `${book.title}-${book.author}`}
                title={book.title}
                author={book.author}
                coverImage={book.coverImage}
                pace={book.pace as Pace}
                tone={book.tone}
                themes={book.themes}
                description={book.description || "No description available"}
                bestFor={book.bestFor}
                isExternal={book.isExternal || false}
              />
            ))}
          </div>
        </div>
      )}
    </div>
  );
};
