import React, { useState } from 'react';
import { ThreadCard } from './ThreadCard';
import { BookCard } from './BookCard';
import { PlusIcon } from '@heroicons/react/24/outline';

export const ThreadPage = () => {
  const [showNewThreadForm, setShowNewThreadForm] = useState(false);
  const [newThreadTitle, setNewThreadTitle] = useState('');
  const [newThreadDescription, setNewThreadDescription] = useState('');

  // Mock data for demonstration
  const threads = [
    {
      title: "Books like Black Mirror",
      description: "Looking for mind-bending sci-fi that makes you question reality",
      upvotes: 234,
      comments: 45,
      timestamp: "2h ago",
      tags: ["Sci-Fi", "Dystopian", "Thriller"]
    },
    {
      title: "Page-turners for slump days",
      description: "Need something to get me out of my reading slump",
      upvotes: 189,
      comments: 32,
      timestamp: "4h ago",
      tags: ["Fast-paced", "Engaging", "Contemporary"]
    }
  ];

  const handleSubmitThread = (e: any) => {
    e.preventDefault();
    // Handle thread submission
    console.log('New thread:', { title: newThreadTitle, description: newThreadDescription });
    setShowNewThreadForm(false);
    setNewThreadTitle('');
    setNewThreadDescription('');
  };

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold text-primary-900">Book Recommendation Threads</h1>
        <button
          onClick={() => setShowNewThreadForm(true)}
          className="btn btn-primary flex items-center"
        >
          <PlusIcon className="w-5 h-5 mr-2" />
          New Thread
        </button>
      </div>

      {/* New Thread Form */}
      {showNewThreadForm && (
        <div className="card">
          <h2 className="text-xl font-semibold mb-4">Start a New Thread</h2>
          <form onSubmit={handleSubmitThread} className="space-y-4">
            <div>
              <label htmlFor="title" className="block text-sm font-medium text-primary-700 mb-1">
                Title
              </label>
              <input
                type="text"
                id="title"
                value={newThreadTitle}
                onChange={(e) => setNewThreadTitle(e.target.value)}
                className="input"
                placeholder="e.g., Books like The Midnight Library"
                required
              />
            </div>
            <div>
              <label htmlFor="description" className="block text-sm font-medium text-primary-700 mb-1">
                Description
              </label>
              <textarea
                id="description"
                value={newThreadDescription}
                onChange={(e) => setNewThreadDescription(e.target.value)}
                className="input min-h-[100px]"
                placeholder="Describe what you're looking for..."
                required
              />
            </div>
            <div className="flex justify-end gap-2">
              <button
                type="button"
                onClick={() => setShowNewThreadForm(false)}
                className="btn btn-secondary"
              >
                Cancel
              </button>
              <button type="submit" className="btn btn-primary">
                Create Thread
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Threads List */}
      <div className="grid gap-4 md:grid-cols-2">
        {threads.map((thread) => (
          <div key={thread.title}>
            <ThreadCard
              title={thread.title}
              description={thread.description}
              upvotes={thread.upvotes}
              comments={thread.comments}
              timestamp={thread.timestamp}
              tags={thread.tags}
            />
          </div>
        ))}
      </div>
    </div>
  );
};
