import React, { useState } from 'react';
import { 
  BookOpenIcon, 
  ClockIcon, 
  HeartIcon, 
  BookmarkIcon, 
  CloudIcon, 
  BoltIcon,
  FireIcon,
  SparklesIcon,
  UserGroupIcon,
  HashtagIcon,
  ChatBubbleBottomCenterTextIcon,
  ChatBubbleLeftRightIcon
} from '@heroicons/react/24/outline';
import { Pace } from '../../types';

interface BookCardProps {
  title: string;
  author: string;
  coverImage?: string;
  pace?: Pace | { type: string; value: string };
  tone?: (string | { type: string; value: string })[];
  themes?: (string | { type: string; value: string })[];
  description?: string;
  bestFor?: string[];
  isExternal?: boolean;
  quote?: string;
  mostQuoted?: string;
}

export const BookCard = ({
  title,
  author,
  coverImage = 'https://via.placeholder.com/150x225?text=No+Cover',
  pace = 'Moderate',
  tone = [],
  themes = [],
  description = 'No description available',
  bestFor = [],
  isExternal = false,
  quote = 'The book is silent on its pages, but speaks volumes in your mind.',
  mostQuoted = 'The journey of a thousand miles begins with a single step.'
}: BookCardProps) => {
  const [isExpanded, setIsExpanded] = useState(false);

  const getPaceIcon = () => {
    const paceValue = typeof pace === 'string' ? pace : pace.value;

    switch (paceValue) {
      case 'Slow':
        return <CloudIcon className="h-4 w-4 text-blue-500" />;
      case 'Fast':
        return <BoltIcon className="h-4 w-4 text-amber-500" />;
      case 'Very Fast':
        return <FireIcon className="h-4 w-4 text-orange-500" />;
      default:
        return <ClockIcon className="h-4 w-4 text-indigo-500" />;
    }
  };

  const paceString = typeof pace === 'string' ? pace : pace.value;

  // Process data to handle both string and object formats
  const processedTone = tone.map(t => typeof t === 'string' ? t : t.value);
  const processedThemes = themes.map(t => typeof t === 'string' ? t : t.value);

  // Maximum items to show in a row before adding "+X more"
  const MAX_ITEMS_PER_ROW = 3;

  return (
    <div className="card hover:shadow-md flex flex-col md:flex-row overflow-hidden">
      {/* Book Cover */}
      <div className="w-full md:w-1/3 max-w-[150px] mx-auto md:mx-0 flex-shrink-0">
        <img 
          src={coverImage} 
          alt={`Cover of ${title}`} 
          className="object-cover w-full h-full rounded shadow-sm"
        />
      </div>

      {/* Book Details */}
      <div className="flex-1 flex flex-col p-4 md:pl-6">
        {/* Title and Bookmark/Like buttons */}
        <div className="flex justify-between items-start">
          <div>
            <h3 className="font-semibold text-lg text-primary-900 dark:text-primary-50">{title}</h3>
            <p className="text-primary-600 dark:text-primary-400 text-sm">{author}</p>
          </div>
          <div className="flex space-x-2">
            <button className="btn-icon text-primary-400 hover:text-primary-700 dark:text-primary-500 dark:hover:text-primary-300">
              <BookmarkIcon className="h-5 w-5" />
            </button>
            <button className="btn-icon text-primary-400 hover:text-primary-700 dark:text-primary-500 dark:hover:text-primary-300">
              <HeartIcon className="h-5 w-5" />
            </button>
          </div>
        </div>

        {/* Reading Pace */}
        <div className="flex items-center mt-3 text-primary-700 dark:text-primary-300">
          {getPaceIcon()}
          <span className="ml-1 text-sm">{paceString} paced</span>
        </div>

        {/* Themes */}
        {processedThemes.length > 0 && (
          <div className="mt-3">
            <div className="flex items-center mb-1" title="Book Themes">
              <HashtagIcon className="h-4 w-4 text-primary-500 mr-1" />
            </div>
            <div className="flex flex-wrap gap-1">
              {processedThemes.slice(0, MAX_ITEMS_PER_ROW).map((theme) => (
                <span key={theme} className="px-2 py-1 text-xs font-medium bg-primary-50 text-primary-600 rounded-full border border-primary-200 dark:bg-primary-800 dark:text-primary-300 dark:border-primary-600">
                  {theme}
                </span>
              ))}
              {processedThemes.length > MAX_ITEMS_PER_ROW && (
                <span className="px-2 py-1 text-xs font-medium bg-primary-100 text-primary-700 rounded-full dark:bg-primary-700 dark:text-primary-200">
                  +{processedThemes.length - MAX_ITEMS_PER_ROW} more
                </span>
              )}
            </div>
          </div>
        )}

        {/* Target Audience / Best For */}
        {bestFor.length > 0 && (
          <div className="mt-2">
            <div className="flex items-center mb-1" title="Target Audience">
              <UserGroupIcon className="h-4 w-4 text-primary-500 mr-1" />
            </div>
            <div className="flex flex-wrap gap-1">
              {bestFor.slice(0, MAX_ITEMS_PER_ROW).map((item) => (
                <span
                  key={item}
                  className="px-2 py-1 text-xs font-medium bg-primary-100 text-primary-700 rounded-full dark:bg-primary-700 dark:text-primary-200"
                >
                  {item}
                </span>
              ))}
              {bestFor.length > MAX_ITEMS_PER_ROW && (
                <span className="px-2 py-1 text-xs font-medium bg-primary-100 text-primary-700 rounded-full dark:bg-primary-700 dark:text-primary-200">
                  +{bestFor.length - MAX_ITEMS_PER_ROW} more
                </span>
              )}
            </div>
          </div>
        )}

        {/* Description */}
        <p className="mt-3 text-primary-600 dark:text-primary-400 text-sm line-clamp-2">
          {description}
        </p>

        {/* Expand/Collapse button */}
        <button
          onClick={() => setIsExpanded(!isExpanded)}
          className="self-start mt-2 text-sm font-medium text-primary-500 hover:text-primary-700 dark:text-primary-400 dark:hover:text-primary-300 flex items-center"
        >
          <BookOpenIcon className="h-4 w-4 mr-1" />
          {isExpanded ? 'Show less' : 'Read more'}
        </button>

        {/* Expanded Content */}
        {isExpanded && (
          <div className="mt-3 animate-slide-down">
            {/* Book Quote */}
            <div className="mb-4 pl-3 border-l-2 border-primary-300 dark:border-primary-600 italic text-primary-600 dark:text-primary-300 text-sm">
              <div className="flex">
                <ChatBubbleLeftRightIcon className="h-4 w-4 text-primary-400 mr-2 flex-shrink-0 mt-1" />
                <p>{quote}</p>
              </div>
            </div>

            {/* Most Quoted */}
            <div className="mb-4">
              <div className="flex items-center mb-2">
                <SparklesIcon className="h-4 w-4 text-amber-500 mr-1" />
                <span className="text-sm font-medium text-primary-700 dark:text-primary-300">Most Quoted</span>
              </div>
              <div className="pl-3 border-l-2 border-amber-300 dark:border-amber-600 text-primary-600 dark:text-primary-300 text-sm">
                <p>{mostQuoted}</p>
              </div>
            </div>

            {/* Writing Style */}
            {processedTone.length > 0 && (
              <div className="mt-2">
                <div className="flex items-center mb-1">
                  <ChatBubbleBottomCenterTextIcon className="h-4 w-4 text-primary-500 mr-1" />
                  <span className="text-sm font-medium text-primary-700 dark:text-primary-300">Style</span>
                </div>
                <div className="flex flex-wrap gap-1">
                  {processedTone.map((t) => (
                    <span key={t} className="px-2 py-1 text-xs font-medium bg-primary-100 text-primary-700 rounded-full dark:bg-primary-700 dark:text-primary-200">
                      {t}
                    </span>
                  ))}
                </div>
              </div>
            )}

            {/* Action buttons */}
            <div className="flex mt-4 space-x-2">
              <button className="btn btn-secondary text-sm">
                Add to List
              </button>
            </div>
          </div>
        )}

        {/* Read Button - Always Visible */}
        <div className="mt-auto pt-3">
          <button className="btn btn-primary text-sm flex items-center justify-center">
            <BookOpenIcon className="h-4 w-4 mr-1" />
