import React, { useState, useEffect, useCallback } from 'react';
import { MagnifyingGlassIcon, AdjustmentsHorizontalIcon } from '@heroicons/react/24/outline';
import debounce from 'lodash/debounce';

interface SearchBarProps {
  onSearch: (query: string, type?: string) => void;
  onMoodSelect?: (mood: string) => void;
  isLoading?: boolean;
}

export const SearchBar = ({ onSearch, onMoodSelect, isLoading = false }: SearchBarProps) => {
  const [query, setQuery] = useState('');
  const [showFilters, setShowFilters] = useState(false);
  const [searchType, setSearchType] = useState('all');
  const [selectedFilters, setSelectedFilters] = useState<string[]>([]);

  // Debounced search function
  const debouncedSearch = useCallback(
    debounce((searchQuery: string, type: string) => {
      if (searchQuery.trim()) {
        onSearch(searchQuery, type);
      }
    }, 300),
    [onSearch]
  );

  useEffect(() => {
    if (query) {
      debouncedSearch(query, searchType);
    }
    return () => {
      debouncedSearch.cancel();
    };
  }, [query, searchType, debouncedSearch]);

  // Define search types
  const searchTypes = [
    { id: 'all', label: 'All' },
    { id: 'title', label: 'Book Titles' },
    { id: 'author', label: 'Authors' },
    { id: 'mood', label: 'Moods' },
    { id: 'theme', label: 'Themes' },
    { id: 'pace', label: 'Reading Style' }
  ];

  // Define moods and reading preferences
  const moods = [
    'Feeling overwhelmed',
    'Need inspiration',
    'Want to escape',
    'Looking for adventure',
    'Feeling nostalgic',
    'Want to learn',
    'Need a laugh',
    'Feeling romantic',
  ];

  const themes = [
    'Love',
    'Adventure',
    'Mystery',
    'Fantasy',
    'Science Fiction',
    'History',
    'Philosophy',
    'Science',
    'Coming of Age',
    'Family',
    'Friendship',
    'Identity',
  ];

  const readingStyles = [
    { id: 'Fast', label: 'Fast-paced reads' },
    { id: 'Moderate', label: 'Balanced pacing' },
    { id: 'Slow', label: 'Slow, thoughtful reads' }
  ];

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (query.trim()) {
      onSearch(query, searchType);
    }
  };

  const handleMoodSelect = (mood: string) => {
    setQuery(mood);
    setSearchType('mood');
    setShowFilters(false);
    setSelectedFilters([...selectedFilters, mood]);
    onSearch(mood, 'mood');
    onMoodSelect?.(mood);
  };

  const handleThemeSelect = (theme: string) => {
    setQuery(theme);
    setSearchType('theme');
    setShowFilters(false);
    setSelectedFilters([...selectedFilters, theme]);
    onSearch(theme, 'theme');
  };

  const handleReadingStyleSelect = (style: string) => {
    setQuery(style);
    setSearchType('pace');
    setShowFilters(false);
    setSelectedFilters([...selectedFilters, style]);
    onSearch(style, 'pace');
  };

  const removeFilter = (filter: string) => {
    setSelectedFilters(selectedFilters.filter(f => f !== filter));
  };

  return (
    <div className="w-full relative space-y-2">
      <form onSubmit={handleSubmit} className="relative">
        <div className="relative flex">
          <div className="flex-grow relative">
            <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Search for books, authors, or moods..."
              className="input pl-10 pr-10 w-full"
              disabled={isLoading}
            />
            <MagnifyingGlassIcon className="w-5 h-5 text-primary-500 absolute left-3 top-1/2 -translate-y-1/2" />
            {isLoading && (
              <div className="absolute right-3 top-1/2 -translate-y-1/2">
                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-primary-600"></div>
              </div>
            )}
          </div>
          <div className="ml-2">
            <select
              value={searchType}
              onChange={(e) => setSearchType(e.target.value)}
              className="input py-2 px-3"
              disabled={isLoading}
            >
              {searchTypes.map(type => (
                <option key={type.id} value={type.id}>{type.label}</option>
              ))}
            </select>
          </div>
          <button
            type="button"
            onClick={() => setShowFilters(!showFilters)}
            className="ml-2 p-2 rounded-lg border border-primary-200 hover:bg-primary-50 text-primary-500 hover:text-primary-700 disabled:opacity-50"
            disabled={isLoading}
          >
            <AdjustmentsHorizontalIcon className="w-5 h-5" />
          </button>
        </div>
      </form>

      {/* Selected Filters */}
      {selectedFilters.length > 0 && (
        <div className="flex flex-wrap gap-2">
          {selectedFilters.map((filter) => (
            <span
              key={filter}
              className="inline-flex items-center px-3 py-1 rounded-full text-sm bg-primary-100 text-primary-700"
            >
              {filter}
              <button
                onClick={() => removeFilter(filter)}
                className="ml-2 text-primary-500 hover:text-primary-700"
              >
                ×
              </button>
            </span>
          ))}
        </div>
      )}

      {/* Advanced Filters */}
      {showFilters && (
        <div className="absolute z-10 mt-2 w-full bg-white rounded-lg shadow-lg border border-primary-200">
          <div className="p-4">
            <div className="grid grid-cols-3 gap-4">
              {/* Moods Section */}
              <div>
                <h3 className="text-sm font-medium text-primary-900 mb-2">Search by mood</h3>
                <div className="flex flex-col gap-2">
                  {moods.map((mood) => (
                    <button
                      key={mood}
                      onClick={() => handleMoodSelect(mood)}
                      className="text-left text-sm text-primary-700 hover:bg-primary-50 p-2 rounded-lg"
                    >
                      {mood}
                    </button>
                  ))}
                </div>
              </div>

              {/* Themes Section */}
              <div>
                <h3 className="text-sm font-medium text-primary-900 mb-2">Search by theme</h3>
                <div className="flex flex-col gap-2">
                  {themes.map((theme) => (
                    <button
                      key={theme}
                      onClick={() => handleThemeSelect(theme)}
                      className="text-left text-sm text-primary-700 hover:bg-primary-50 p-2 rounded-lg"
                    >
                      {theme}
                    </button>
                  ))}
                </div>
              </div>

              {/* Reading Style Section */}
              <div>
                <h3 className="text-sm font-medium text-primary-900 mb-2">Search by reading style</h3>
                <div className="flex flex-col gap-2">
                  {readingStyles.map((style) => (
                    <button
                      key={style.id}
                      onClick={() => handleReadingStyleSelect(style.id)}
                      className="text-left text-sm text-primary-700 hover:bg-primary-50 p-2 rounded-lg"
                    >
                      {style.label}
                    </button>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
