import React from 'react';
import { BookOpenIcon, ClockIcon, HeartIcon, BookmarkIcon } from '@heroicons/react/24/outline';

interface BookCardProps {
  title: string;
  author: string;
  coverImage: string;
  pace: 'Fast' | 'Moderate' | 'Slow';
  tone: string[];
  themes: string[];
  description: string;
  bestFor: string[];
}

export const BookCard = ({
  title,
  author,
  coverImage,
  pace,
  tone,
  themes,
  description,
  bestFor,
}: BookCardProps) => {
  return (
    <div className="card hover:shadow-md transition-shadow">
      <div className="flex gap-4">
        {/* Book Cover */}
        <div className="w-24 h-36 flex-shrink-0">
          <img
            src={coverImage}
            alt={`${title} cover`}
            className="w-full h-full object-cover rounded-lg"
          />
        </div>

        {/* Book Info */}
        <div className="flex-1">
          <h3 className="text-lg font-semibold">{title}</h3>
          <p className="text-primary-600 mb-2">{author}</p>

          {/* Quick Tags */}
          <div className="flex items-center gap-2 mb-2">
            <ClockIcon className="w-4 h-4 text-primary-500" />
            <span className="text-sm text-primary-600">{pace} paced</span>
          </div>

          {/* Tone & Themes */}
          <div className="flex flex-wrap gap-2 mb-2">
            {tone && tone.map((t) => (
              <span key={t} className="text-xs bg-primary-100 text-primary-700 px-2 py-1 rounded-full">
                {t}
              </span>
            ))}
            {themes && themes.map((theme) => (
              <span key={theme} className="text-xs bg-primary-100 text-primary-700 px-2 py-1 rounded-full">
                {theme}
              </span>
            ))}
          </div>

          {/* Review Snippet */}
          <p className="text-sm text-primary-700 mb-2 line-clamp-2">{description}</p>

          {/* Best For */}
          <div className="flex flex-wrap gap-2">
            {bestFor && bestFor.map((scenario) => (
              <span key={scenario} className="text-xs bg-primary-50 text-primary-600 px-2 py-1 rounded-full">
                {scenario}
              </span>
            ))}
          </div>

          {/* Actions */}
          <div className="flex gap-2 mt-3">
            <button className="btn btn-primary text-sm">
              <BookOpenIcon className="w-4 h-4 mr-1" />
              Read
            </button>
            <button className="btn btn-secondary text-sm">
              <BookmarkIcon className="w-4 h-4 mr-1" />
              Save
            </button>
            <button className="btn btn-secondary text-sm">
              <HeartIcon className="w-4 h-4" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};
