import React from 'react';
import { motion } from 'framer-motion';

interface BookDNAProps {
  pace: string;
  tone: string[];
  themes: string[];
  size?: 'sm' | 'md' | 'lg';
}

export const BookDNA = ({ pace, tone, themes, size = 'md' }: BookDNAProps) => {
  // Define size configurations
  const sizeConfig = {
    sm: { container: 60, center: 30, rings: [15, 20, 25] },
    md: { container: 80, center: 40, rings: [20, 25, 30] },
    lg: { container: 100, center: 50, rings: [25, 30, 35] }
  };

  const config = sizeConfig[size];

  // Color mapping for different attributes
  const paceColors = {
    'Fast': '#ef4444', // Red for fast-paced
    'Moderate': '#f59e0b', // Amber for moderate
    'Slow': '#10b981', // Emerald for slow/thoughtful
  };

  const toneColors = [
    '#8b5cf6', // Purple
    '#06b6d4', // Cyan  
    '#f59e0b', // Amber
    '#ef4444', // Red
    '#10b981', // Emerald
    '#f97316', // Orange
    '#ec4899', // Pink
    '#6366f1', // Indigo
  ];

  const themeColors = [
    '#3b82f6', // Blue
    '#8b5cf6', // Purple
    '#10b981', // Emerald
    '#f59e0b', // Amber
    '#ef4444', // Red
    '#06b6d4', // Cyan
    '#f97316', // Orange
    '#ec4899', // Pink
  ];

  // Generate unique pattern based on book attributes
  const generateDNAPattern = () => {
    const elements = [];

    // Central pace indicator
    const paceColor = paceColors[pace as keyof typeof paceColors] || paceColors.Moderate;
    elements.push(
      <motion.circle
        key="pace-center"
        cx={config.center}
        cy={config.center}
        r="8"
        fill={paceColor}
        initial={{ scale: 0 }}
        animate={{ scale: 1 }}
        transition={{ delay: 0.2, type: "spring", stiffness: 300 }}
      />
    );

    // Tone rings - inner ring
    tone.slice(0, 3).forEach((_, index) => {
      const angle = (index * 120) * (Math.PI / 180);
      const radius = config.rings[0];
      const x = config.center + radius * Math.cos(angle);
      const y = config.center + radius * Math.sin(angle);
      const color = toneColors[index % toneColors.length];

      elements.push(
        <motion.circle
          key={`tone-${index}`}
          cx={x}
          cy={y}
          r="4"
          fill={color}
          initial={{ scale: 0, opacity: 0 }}
          animate={{ scale: 1, opacity: 0.8 }}
          transition={{ delay: 0.4 + index * 0.1, type: "spring" }}
        />
      );
    });

    // Theme elements - outer ring
    themes.slice(0, 6).forEach((_, index) => {
      const angle = (index * 60) * (Math.PI / 180);
      const radius = config.rings[2];
      const x = config.center + radius * Math.cos(angle);
      const y = config.center + radius * Math.sin(angle);
      const color = themeColors[index % themeColors.length];

      // Create geometric shapes based on theme index
      if (index % 3 === 0) {
        // Triangles
        const size = 3;
        const points = `${x},${y-size} ${x-size},${y+size} ${x+size},${y+size}`;
        elements.push(
          <motion.polygon
            key={`theme-triangle-${index}`}
            points={points}
            fill={color}
            initial={{ scale: 0, rotate: 0 }}
            animate={{ scale: 1, rotate: 360 }}
            transition={{ delay: 0.6 + index * 0.1, duration: 0.8 }}
          />
        );
      } else if (index % 3 === 1) {
        // Squares
        elements.push(
          <motion.rect
            key={`theme-square-${index}`}
            x={x - 3}
            y={y - 3}
            width="6"
            height="6"
            fill={color}
            initial={{ scale: 0, rotate: 45 }}
            animate={{ scale: 1, rotate: 0 }}
            transition={{ delay: 0.6 + index * 0.1, type: "spring" }}
          />
        );
      } else {
        // Circles
        elements.push(
          <motion.circle
            key={`theme-circle-${index}`}
            cx={x}
            cy={y}
            r="3"
            fill={color}
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ delay: 0.6 + index * 0.1, type: "spring", stiffness: 400 }}
          />
        );
      }
    });

    // Connecting lines for complexity
    if (tone.length > 1) {
      tone.slice(0, 3).forEach((_, index) => {
        const nextIndex = (index + 1) % Math.min(3, tone.length);
        const angle1 = (index * 120) * (Math.PI / 180);
        const angle2 = (nextIndex * 120) * (Math.PI / 180);
        const radius = config.rings[0];

        const x1 = config.center + radius * Math.cos(angle1);
        const y1 = config.center + radius * Math.sin(angle1);
        const x2 = config.center + radius * Math.cos(angle2);
        const y2 = config.center + radius * Math.sin(angle2);

        elements.push(
          <motion.line
            key={`connection-${index}`}
            x1={x1}
            y1={y1}
            x2={x2}
            y2={y2}
            stroke={toneColors[index % toneColors.length]}
            strokeWidth="1"
            opacity="0.3"
            initial={{ pathLength: 0 }}
            animate={{ pathLength: 1 }}
            transition={{ delay: 0.8 + index * 0.1, duration: 0.6 }}
          />
        );
      });
    }

    return elements;
  };

  return (
    <div className="relative">
      <motion.svg
        width={config.container}
        height={config.container}
        viewBox={`0 0 ${config.container} ${config.container}`}
        className="drop-shadow-lg"
        whileHover={{ scale: 1.1 }}
        transition={{ type: "spring", stiffness: 400 }}
      >
        {/* Background glow */}
        <defs>
          <radialGradient id="dna-glow" cx="50%" cy="50%" r="50%">
            <stop offset="0%" stopColor="rgba(139, 92, 246, 0.1)" />
            <stop offset="100%" stopColor="rgba(139, 92, 246, 0)" />
          </radialGradient>
        </defs>

        <circle
          cx={config.center}
          cy={config.center}
          r={config.center - 5}
          fill="url(#dna-glow)"
        />

        {/* DNA Pattern */}
        {generateDNAPattern()}

        {/* Outer ring guide */}
        <motion.circle
          cx={config.center}
          cy={config.center}
          r={config.rings[2]}
          fill="none"
          stroke="rgba(139, 92, 246, 0.1)"
          strokeWidth="1"
          initial={{ scale: 0, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ delay: 1, duration: 0.5 }}
        />
      </motion.svg>

      {/* Tooltip on hover */}
      <motion.div
        className="absolute -bottom-8 left-1/2 transform -translate-x-1/2 bg-gray-900 text-white text-xs px-2 py-1 rounded opacity-0 pointer-events-none whitespace-nowrap"
        whileHover={{ opacity: 1 }}
      >
        {pace} • {tone.length} tones • {themes.length} themes
      </motion.div>
    </div>
  );
};
